import { observer } from "mobx-react-lite";
import { useContext, useState } from "react";
import { Context } from "..";
import { getchat } from "../http/chatApi";
import CreateChatComponent from "./CreateChatComponent";



const ChatBar = observer(() => {
	const { chats, Activeuser } = useContext(Context)
	const [userId, setUserId] = useState()
	setUserId = () => {
		userId = id
	}
	setUserId(Activeuser.userId)
	console.log(userId)
	const a = getchat(userId).then(() => {
		console.log(a)
	})



	return (
		<div className="ChatBar">
			<CreateChatComponent />
			{chats.aliveChats.map(chat =>
				<div key={chat.id} onClick={() => { chats.setSelectedChat(chat) }} className={chat.id === chats.selectedChat.id ? "ChatCard active" : "ChatCard"} >{chat.host}</div>
			)}

		</div>

	)
})

export default ChatBar
