import { observer } from "mobx-react-lite";
import { useContext } from "react";
import { Context } from "..";
import { getmessages } from "../http/chatApi";
import CreateChatComponent from "./CreateChatComponent";



const ChatBar = observer(() => {
	const { chats, controler } = useContext(Context)
	async function click(chat) {
		chats.setSelectedChat(chat)
		console.log(chats.selectedChat.id)
		const a = await getmessages(chats.selectedChat.id)
		console.log(typeof (a))
		controler.setMessage(a)
	}
	return (
		<div className="ChatBar">
			<CreateChatComponent />
			{chats.aliveChats.map(chat =>
				<div key={chat.id} onClick={() => { click(chat) }} className={chat.id === chats.selectedChat.id ? "ChatCard active" : "ChatCard"} >{chat.host}</div>
			)}

		</div>

	)
})

export default ChatBar
