import { $authHost, $host } from "./index";
import jwt_decode from "jwt-decode"

export const registration = async (email, password, nickname) => {
	const { data } = await $host.post("/user/registration", { email, password, nickname })
	return jwt_decode(data.token)
}
export const login = async (email, password) => {
	const { data } = await $host.post("/user/registration", { email, password })
	return jwt_decode(data.token)
}
export const check = async (email, password, nickname) => {
	const response = await $host.post("/user/registration", { email, password })
	return response
}
