import React from "react";
import { observer } from "mobx-react-lite";
import { Link, useLocation } from "react-router-dom";
import { LOGIN_ROUTE, REGISTRATION_ROUTE } from "../utils/const";
import { registration } from "../http/userApi";
const Authentication = observer(() => {
	const location = useLocation()
	const isLogin = location.pathname === LOGIN_ROUTE

	const click = async () => {
		if (isLogin) {

		} else {

		}
		const response = await registration()
		console.log(response);


	}
	return (

		<div className="auth">
			<form className="auth_form">
				<h2>{isLogin ? "Авторизация" : "Регистрация"}</h2>
				<input placeholder="Ваш Email" type="login"></input>
				<input placeholder="Ваш пароль" type="password"></input>
				{!isLogin ? <input placeholder="Ваш никнейм" type=""></input> : ""}
				<span>{isLogin ? "Нет аккаунта? " : "Уже есть аккаунт? "}
					{isLogin ?
						<Link to={REGISTRATION_ROUTE}>зарегестрируйтесь!</Link>
						:
						<Link to={LOGIN_ROUTE}>Войдите тут</Link>
					}
				</span>
				{isLogin ?
					<button type="submit" className="Login_button">Войти</button>
					:
					<button type="submit" className="Login_button">Зарегестрировать</button>
				}
			</form>
		</div>

	)
})

export default Authentication
