import { observer } from "mobx-react-lite";
import { useContext } from "react";
import { Context } from "..";



const ChatBar = observer(() => {
	const { chats } = useContext(Context)


	return (
		<div className="ChatBar">
			<span>ChatBar</span>
			{chats.aliveChats.map(aliveChats =>
				<div key={aliveChats.id} className="ChatCard" active={aliveChats == chats.selectedChat} onClick={() => chats.setSelectedChat(aliveChats)} >{aliveChats.host}</div>

			)}

		</div>
	)
})

export default ChatBar
