import { observer } from "mobx-react-lite"
import { useContext } from "react"
import { Context } from ".."
import SendMessage from "./SendMessage"


const MesPanel = observer(() => {
	const { controler, Activeuser } = useContext(Context)
	console.log(Activeuser)
	const userId = Activeuser.user.id
	console.log(userId)

	return (
		<section className="messagepanel" >
			<>	{
				controler.messages.map(message =>
					<div key={message.id} style={userId === message.userId ? { alignSelf: "flex-start" } : { alignSelf: "flex-end" }}>{message.text}</div>
				)
			}
				<SendMessage />
			</>
		</section >
	)

})
export default MesPanel
