import { makeAutoObservable } from "mobx";

export default class ChatStore {
	constructor() {
		this._chats = [
			{}
		]
		makeAutoObservable(this)
	}

	setChats(chat) {
		this._chats.push(chat)
	}

	get chats() {
		return this.chats
	}
}
