import { makeAutoObservable } from "mobx";

export default class UserStore {
	constructor() {
		this._isAuth = false
		this._user = { id, email, nickname }
		makeAutoObservable(this)
	}

	setIsAuth(bool) {
		this._isAuth = bool
	}
	setUser({ user }) {
		this._user.id = user.id
		this._user.email = user.email
		this._user.nickname = user.nickname
	}
	get isAuth() {
		return this._isAuth
	}
	get user() {
		return this._user
	}
}
