import React from "react";
import { observer } from "mobx-react-lite";
import { useLocation } from "react-router-dom";
import { LOGIN_ROUTE } from "../utils/const";
const Authentication = observer(() => {
	const location = useLocation()
	const isLogin = location.pathname === LOGIN_ROUTE
	return (

		<div className="auth">
			<form className="auth_form">

				<h2>{isLogin ? "Авторизация" : "Регистрация"}</h2>
				<input placeholder="Ваш Email" type="login"></input>
				<input placeholder="Ваш пароль" type="password"></input>
				{!isLogin ? <input placeholder="Ваш никнейм" type=""></input> : ""}
				<span>{isLogin ? "Нет аккаунта?" : "Уже есть аккаунт?"} <span>{isLogin ? "зарегестрируйтесь!" : "Войдите тут"}</span></span>
				<button type="submit" className="Login_button">Войти</button>

			</form>
		</div >

	)
})

export default Authentication
