import { observer } from "mobx-react-lite";
import { useContext } from "react";
import { Context } from "..";
import { getmessages } from "../http/chatApi";




const ChatBar = observer(() => {
	const { chats, controler } = useContext(Context)
	async function click(chat) {
		chats.setSelectedChat(chat)
		const a = await getmessages(chats.selectedChat.id)
		controler.setMessages(a)
		console.log(chats.selectedChat)
	}
	return (

		<div className="ChatBar">
			<form className="CreateChatComponent">
				<input type="text" className="CreateChatComponent_input"></input>
				<button type="submit" className="CreateChatComponent_button">+</button>
			</form>
			{chats.aliveChats.map(chat =>
				<div key={chat.id} onClick={() => { click(chat) }} className={chat.id === chats.selectedChat.id ? "ChatCard active" : "ChatCard"} >{chat.nickname}</div>
			)}

		</div>

	)
})

export default ChatBar
