import { makeAutoObservable } from "mobx";

export default class UserStore {
	constructor() {
		this._isAuth = false
		this._userId = ''
		this._userEmail = ''
		this._userNickname = ''
		makeAutoObservable(this)
	}

	setIsAuth(bool) {
		this._isAuth = bool
	}
	setUser(user) {
		this._userId = user.id
		this._userEmail = user.email
		this._userNickname = user.nickname
	}
	get isAuth() {
		return this._isAuth
	}
	get userId() {
		return this._userId
	}
	get userEmail() {
		return this._userEmail
	}
	get userNickname() {
		return this._userNickname
	}
}
