import { makeAutoObservable } from "mobx";

export default class ChatStore {
	constructor() {
		this._chats = [
			{ id: 1, host: "test" },
			{ id: 2, host: "test2" },
			{ id: 3, host: "test3" },
			{ id: 4, host: "test4" }
		]
		makeAutoObservable(this)
	}

	setChat(chat) {
		this._chats.push(chat)
	}

	get chat() {
		return this.chat
	}
}
