import { makeAutoObservable } from "mobx";

export default class MessageStore {
	constructor() {
		this._messages = [
			{ id: 1, text: "test", chatId: 1, userId: 1 },
			{ id: 2, text: "test", chatId: 1, userId: 2 },
			{ id: 3, text: "test", chatId: 1, userId: 1 },
			{ id: 4, text: "test", chatId: 1, userId: 2 },
		]

		makeAutoObservable(this)
	}

	setMessages(message) {
		this._messages.push(message)
	}

	get messages() {
		return this._messages
	}

}
