import { observer } from "mobx-react-lite";
import { useContext, useState } from "react";
import { Context } from "..";



const ChatBar = observer(() => {
	const { chats } = useContext(Context)
	const [isActive, setActive] = useState("false")
	const toggleClass = () => {
		setActive(!isActive)
	}

	return (
		<div className="ChatBar">
			<span>ChatBar</span>
			{chats.aliveChats.map(chat =>
				<div key={chat.id} className={"ChatCard" + isActive ? "active" : null} onClick={() => { chats.setSelectedChat(chat) }} active={chat.id === chats.selectedChat.id}>{chat.host}</div>

			)}

		</div>
	)
})

export default ChatBar
