import { $host } from "./index";

export const createchat = async (nickname1, nickname2) => {
	const chat = await $host.post("/chat/", { nickname1, nickname2 })
	console.log(chat);
	return chat
}
export async function getchat(userId) {
	const chat = await $host.get("/chat/", { "userId": 17 })
	return chat
}

export const sendmessage = async (text, userId, chatId) => {
	const message = await $host.post("/chat/message", { text, userId, chatId })
	console.log(message);
	return message
}

export const getmessages = async (chatId) => {
	const messages = await $host.get("/chat/message", { chatId })
	console.log(messages);
	return messages
}
